mapper.update:
update board_shop
set title=#{title}, content=#{content}, imagePath=#{imagePath}, categorie=#{categorie}, telNum=#{telNum}, addr=#{addr}, startTime=#{startTime}, endTime=#{endTime}
where num=#{num}

mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT * FROM (SELECT s.num, s.title, s.content, s.imagePath, s.categorie, s.reviewCount, s.startTime, s.endTime, s.telNum, s.addr, r.grade from board_shop s, (SELECT ref_group, avg(grade) grade FROM board_shop_review where deleted = 'no' group by ref_group) r where s.num = r.ref_group(+))
ORDER BY num DESC) result1)
WHERE rnum >= #{startRowNum} AND rnum <= #{endRowNum}

   		select result1.*
	   		from	(select num,title,content,imagePath,categorie,
	      		reviewCount,startTime, endTime,telNum,addr
	     	from board_shop

	     	) result1
     	where num=#{num}
