mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT num, writer, title, content, ref_group, review_group, deleted, imagePath, grade, TO_CHAR(board_shop_review.regdate, 'YY.MM.DD HH24:MI') AS regdate FROM board_shop_review
WHERE content LIKE '%'||#{content}||'%'
ORDER BY num ASC) result1) WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}

SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT num, writer, title, content, ref_group, review_group, deleted, imagePath, grade, TO_CHAR(board_shop_review.regdate, 'YY.MM.DD HH24:MI') AS regdate, profile FROM board_shop_review
WHERE title LIKE '%'||#{title}||'%'
INNER JOIN users ON board_shop_review.writer = users.id WHERE ref_group = #{ref_group} ORDER BY review_group ASC, num ASC) result1) WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}

mapper.update:
UPDATE board_shop_review
SET deleted = 'yes'
WHERE num = #{num}

UPDATE board_shop_review
SET title = #{title}, content = #{content}
WHERE num = #{num}

mapper.select:
SELECT num, writer, title, content, ref_group, review_group, deleted, TO_CHAR(regdate, 'YY.MM.DD HH24:MI') AS regdate, imagePath
FROM board_shop_review
WHERE num = #{num}

SELECT NVL(MAX(ROWNUM), 0)
FROM board_shop_review
WHERE ref_group = #{ref_group}
