mapper.select:
SELECT id, pwd, profile, email, regdate, ban, loggedIn
FROM users
WHERE id=#{id}

mapper.update:
UPDATE users
SET pwd=#{newPwd}
WHERE id=#{id}

UPDATE users
SET email=#{email }, profile=#{profile, jdbcType=VARCHAR}, loggedIn=#{loggedIn}
WHERE id=#{id }

UPDATE users
SET ban='BAN'
WHERE id=#{id }

mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT id, pwd, email, regdate, ban FROM users
WHERE id LIKE '%'||#{id}||'%'
ORDER BY regdate DESC) result1) WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}
